mapper.select:
select 

from sys_department


    id = #{id,jdbcType=INTEGER}


    and name = #{name,jdbcType=VARCHAR}


    and parent_id = #{parentId,jdbcType=INTEGER}


    and code = #{code,jdbcType=VARCHAR}


    and sort = #{sort,jdbcType=INTEGER}


    and remark = #{remark,jdbcType=VARCHAR}


    and status = #{status,jdbcType=TINYINT}


    and create_by = #{createBy,jdbcType=INTEGER}


    and create_date = #{createDate,jdbcType=TIMESTAMP}


    and update_by = #{updateBy,jdbcType=INTEGER}


    and update_date = #{updateDate,jdbcType=TIMESTAMP}


    and del_flag = #{delFlag,jdbcType=INTEGER}

mapper.insert:
insert into sys_department (id, name, parent_id, 
  code, sort, remark, 
  status, create_by, create_date, 
  update_by, update_date, del_flag
  ) values

   (#{item.id,jdbcType=INTEGER}, #{item.name,jdbcType=VARCHAR}, #{item.parentId,jdbcType=INTEGER}, 
    #{item.code,jdbcType=VARCHAR}, #{item.sort,jdbcType=INTEGER}, #{item.remark,jdbcType=VARCHAR}, 
    #{item.status,jdbcType=TINYINT}, #{item.createBy,jdbcType=INTEGER}, #{item.createDate,jdbcType=TIMESTAMP}, 
    #{item.updateBy,jdbcType=INTEGER}, #{item.updateDate,jdbcType=TIMESTAMP}, #{item.delFlag,jdbcType=INTEGER}
    )

mapper.delete:
delete from sys_department


    id = #{id,jdbcType=INTEGER}


    and name = #{name,jdbcType=VARCHAR}


    and parent_id = #{parentId,jdbcType=INTEGER}


    and code = #{code,jdbcType=VARCHAR}


    and sort = #{sort,jdbcType=INTEGER}


    and remark = #{remark,jdbcType=VARCHAR}


    and status = #{status,jdbcType=TINYINT}


    and create_by = #{createBy,jdbcType=INTEGER}


    and create_date = #{createDate,jdbcType=TIMESTAMP}


    and update_by = #{updateBy,jdbcType=INTEGER}


    and update_date = #{updateDate,jdbcType=TIMESTAMP}


    and del_flag = #{delFlag,jdbcType=INTEGER}
