mapper.select:
select

from sys_user


    id = #{id,jdbcType=INTEGER}


    and department_id = #{departmentId,jdbcType=INTEGER}


    and login_name = #{loginName,jdbcType=VARCHAR}


    and password = #{password,jdbcType=VARCHAR}


    and no = #{no,jdbcType=VARCHAR}


    and name = #{name,jdbcType=VARCHAR}


    and email = #{email,jdbcType=VARCHAR}


    and phone = #{phone,jdbcType=VARCHAR}


    and mobile = #{mobile,jdbcType=VARCHAR}


    and user_type = #{userType,jdbcType=INTEGER}


    and photo = #{photo,jdbcType=VARCHAR}


    and login_ip = #{loginIp,jdbcType=VARCHAR}


    and login_date = #{loginDate,jdbcType=TIMESTAMP}


    and login_flag = #{loginFlag,jdbcType=INTEGER}


    and create_by = #{createBy,jdbcType=INTEGER}


    and create_date = #{createDate,jdbcType=TIMESTAMP}


    and update_by = #{updateBy,jdbcType=INTEGER}


    and update_date = #{updateDate,jdbcType=TIMESTAMP}


    and remarks = #{remarks,jdbcType=VARCHAR}


    and del_flag = #{delFlag,jdbcType=INTEGER}


    and salt = #{salt,jdbcType=VARCHAR}

mapper.delete:
delete from sys_user


    id = #{id,jdbcType=INTEGER}


    and department_id = #{departmentId,jdbcType=INTEGER}


    and login_name = #{loginName,jdbcType=VARCHAR}


    and password = #{password,jdbcType=VARCHAR}


    and no = #{no,jdbcType=VARCHAR}


    and name = #{name,jdbcType=VARCHAR}


    and email = #{email,jdbcType=VARCHAR}


    and phone = #{phone,jdbcType=VARCHAR}


    and mobile = #{mobile,jdbcType=VARCHAR}


    and user_type = #{userType,jdbcType=INTEGER}


    and photo = #{photo,jdbcType=VARCHAR}


    and login_ip = #{loginIp,jdbcType=VARCHAR}


    and login_date = #{loginDate,jdbcType=TIMESTAMP}


    and login_flag = #{loginFlag,jdbcType=INTEGER}


    and create_by = #{createBy,jdbcType=INTEGER}


    and create_date = #{createDate,jdbcType=TIMESTAMP}


    and update_by = #{updateBy,jdbcType=INTEGER}


    and update_date = #{updateDate,jdbcType=TIMESTAMP}


    and remarks = #{remarks,jdbcType=VARCHAR}


    and del_flag = #{delFlag,jdbcType=INTEGER}


    and salt = #{salt,jdbcType=VARCHAR}

mapper.select:
select
u.*,d.name as department_name
from sys_user u
left join sys_department d on d.id=u.department_id


    u.id = #{id,jdbcType=INTEGER}


    and u.department_id = #{departmentId,jdbcType=INTEGER}


    and u.login_name like CONCAT('%',#{loginName,jdbcType=VARCHAR},'%')


    and u.password = #{password,jdbcType=VARCHAR}


    and u.no = #{no,jdbcType=VARCHAR}


    and u.name like CONCAT('%',#{name,jdbcType=VARCHAR},'%')


    and u.email = #{email,jdbcType=VARCHAR}


    and u.phone = #{phone,jdbcType=VARCHAR}


    and u.mobile = #{mobile,jdbcType=VARCHAR}


    and u.user_type = #{userType,jdbcType=INTEGER}


    and u.photo = #{photo,jdbcType=VARCHAR}


    and u.login_ip = #{loginIp,jdbcType=VARCHAR}


    and u.login_date = #{loginDate,jdbcType=TIMESTAMP}


    and u.login_flag = #{loginFlag,jdbcType=INTEGER}


    and u.create_by = #{createBy,jdbcType=INTEGER}


    and u.create_date = #{createDate,jdbcType=TIMESTAMP}


    and u.update_by = #{updateBy,jdbcType=INTEGER}


    and u.update_date = #{updateDate,jdbcType=TIMESTAMP}


    and u.remarks = #{remarks,jdbcType=VARCHAR}


    and u.del_flag = #{delFlag,jdbcType=INTEGER}


    and u.salt = #{salt,jdbcType=VARCHAR}
